function setChange(){
	if(document.f.s1.value=="change")
	{
		document.all.change.style.display = "block";
		document.all.name.style.display = "none";
		document.all.price.style.display = "none";
		document.all.nameandprice.style.display = "none";
	}
	if(document.f.s1.value=="name")
	{
		document.all.name.style.display = "block";
		document.all.price.style.display = "none";
		document.all.nameandprice.style.display = "none";
		document.all.change.style.display = "none";
	}
	if(document.f.s1.value=="price")
	{
		document.all.price.style.display = "block";
		document.all.name.style.display = "none";
		document.all.nameandprice.style.display = "none";
		document.all.change.style.display = "none";
	}
		if(document.f.s1.value=="nameandprice")
	{
		document.all.nameandprice.style.display = "block";
		document.all.price.style.display = "none";
		document.all.name.style.display = "none";
		document.all.change.style.display = "none";
	}
}